
import smtplib, ssl
import sqlite3
import os


def send_email(message):
    host = "smtp.gmail.com"
    port = 465

    username = "guyex1996@gmail.com"
    password= os.getenv("PASSWORD")

    receiver = "guyex1996@gmail.com"
    context = ssl.create_default_context()
    subject="Subject: NEW MUSIC EVENT\n"
    message=subject+'\n'+message
    with smtplib.SMTP_SSL(host, context=context) as server:
        server.login(username, password)
        server.sendmail(username, receiver, message)

    print("Email was sent") 


def storage(info):
    info=info.strip()
    info=tuple(info.split(','))
    connection=sqlite3.connect("data.db")  
    cursor=connection.cursor()
    cursor.execute("SELECT * FROM events") 
    rows=cursor.fetchall()
    if info not in rows:
        row=[info]
        cursor.executemany("INSERT INTO events VALUES(?,?,?)",row) 
        connection.commit() 
        
        return ["True", info]
